package While_DoWhile_loop;

public class Day08_WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int B = 1;
		System.out.println(" the value of B before it enters the loop is" +  ' ' +B);
		while (B < 5 ) {
			System.out.println("Hello AImal");
			B++;
		}
		System.out.println("the value of B after the loop is" + ' ' + B);
		
	
		
		
	}

}
